var filter = L.control({position:'topleft'});

filter.onAdd = function (map) {
    this._div = L.DomUtil.create('div', 'filter'); // create a div with a class "info"
    this.update();
    return this._div;
};

// method that we will use to update the control based on feature properties passed
filter.update = function (year) {
    
    this._div.innerHTML = ('<div class="info">Year <a href=# onclick=point(0) class="previous round"><div class="myclass">&#8249;</div></a> '+ye+' <a href="#" onclick=point(1) class="next round"><div class="myclass">&#8250;</div></a></div>'
        );
};

filter.addTo(mymap);

function newstyle(e){

    geojson.eachLayer(function (layer) {  
  if(layer.feature.properties.year == ye) {    
    layer.setStyle({fillColor :'red',fillOpacity: 0.7}) 
  }
  else
    geojson.resetStyle(layer);
}).addTo(mymap);
    
}

function newlayer()
{
   var swapkey1;
   var swapkey2;
   for (i in Object.keys(geojson._layers)){
    var Key = Object.keys(geojson._layers)[i];
	if (ye == geojson._layers[Key].feature.properties.year)
    {
		swapkey1 = Key;
	}
}

for (i in Object.keys(geojson._layers)){
    var Key = Object.keys(geojson._layers)[i];
	if (geojson._layers[swapkey1].feature.id== geojson._layers[Key].feature.id)
    {
		swapkey2 = Key;
	}
    
}

var temp = geojson._layers[swapkey1].feature.properties;
geojson._layers[swapkey1].feature.properties = geojson._layers[swapkey2].feature.properties;
geojson._layers[swapkey2].feature.properties = temp;
   
}
function point(e){
        if(ye =="All")
        {
            ye = years[years.length-1];
        }
        else if (e == 0){
            if(Math.min.apply(Math, years) < ye){
                for (var i =0;i<years.length;i++)
                {
                    if(ye == years[i]){
                        ye = years[i-1];
                        break;
                    }
                }
            }
        }
        else{
            if(Math.max.apply(Math, years) > ye){
            for (var i =0;i<years.length;i++)
                {
                    if(ye == years[i]){
                        ye = years[i+1];
                        break;
                    }
                }
            }
        }
        newlayer();
        newstyle();
        
        filter.update();
    }


function resetHighlight(e) {
    geojson.resetStyle(e.target);
    if(close == 0){
        info.update('None');
    }
    close = 0
}
var geojson;
geojson = L.geoJson(country);



function makeStablePopUp(e) {
    close = 1
}


function onEachFeature(feature, layer) {
    layer.on({
        mouseover: highlightFeature,
        mouseout: resetHighlight,
        click: makeStablePopUp
    });
}

geojson = L.geoJson(country, {
    style: style,
    onEachFeature: onEachFeature
}).addTo(mymap); 
